package com.review1;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 100;
		int b = 1900;
		
		String x = " Hello ";
		String y = "Java";
		
		System.out.println(a+b+x+y);
		System.out.println(x+y+a+b);
		System.out.println(x+y+(a+b));
		
		System.out.println("The value of c :" +(a+b));
		
	}
	

}
